import React from 'react';

import FormInput from '../form-input/form-input.component';
import CustonButton from '../custombutton/custombutton.component';

import { auth, createUserProfileDocument } from '../../firebase/firebase.utils';

import './signup.style.scss';
import SignIn from '../signin/signin.component';


class SignUp extends React.Component {
    constructor(props){
        super(props);

        this.state = {

            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''

        }
    }

    handleSubmit = async event => {
        event.preventDefault();
        const {displayName, email, password, confirmPassword} = this.state;
        if(password != confirmPassword){
            alert('Passwords do not match');
            return;
        }

        try {

            const { user } = await auth.createUserWithEmailAndPassword(email, password);

            await createUserProfileDocument(user, { displayName });

            this.setState({

                displayName: '',
                email: '',
                password: '',
                confirmPassword: ''

            });

        } catch (error) {
            console.error(error);
        }
    }

    handleChange = event => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    render(){
        const {displayName, email, password, confirmPassword} = this.state;
        return (
            <div className='sign-up'>
                <h2 className='title'>I do not have an account</h2>
                <span>Sign up with your email and password</span>
                <form className='sign-up-form' onSubmit={this.handleSubmit}>
                    <FormInput type='text' name='displayName' value={displayName} onChange={handleChange} label='Display Name' required/>
                    <FormInput type='email' name='email' value={email} onChange={handleChange} label='Email' required/>
                    <FormInput type='password' name='password' value={password} onChange={handleChange} label='Password' required/>
                    <FormInput type='password' name='password' value={confirmPassword} onChange={handleChange} label='Confirm Password' required/>
                    <CustonButton type='submit'>SIGN UP</CustonButton>
                </form>
            </div>
        )
    }
}

export default SignUp;
